    private void b1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b1ActionPerformed
        if(r1.isSelected()==true) {
            t1.setText("Sorry,your answer is wrong." +
                    "You slip to Rs.12,50,000.");
            t2.setText("Correct Answer is Option 4");
            b4.setText("GAME OVER");
            ls1.setSelectedIndex(4);
        } else
            if(r4.isSelected()==true) {
                t1.setText("Congrats,your answer is correct." +
                        "You won Rs.50,00,000");
                b4.setVisible(false);
                b3.setEnabled(true);
                ls1.setSelectedIndex(2);
            } else
                if(r3.isSelected()==true) {
                    t1.setText("Sorry,your answer is wrong." +
                            "You slip to Rs.12,50,000.");
                    t2.setText("Correct Answer is Option 4");
                    b4.setText("GAME OVER");
                    ls1.setSelectedIndex(4);
                } else

                {
            t1.setText("Sorry,your answer is wrong." +
                    "You slip to Rs.12,50,000.");
            t2.setText("Correct Answer is Option 4");
            b4.setText("GAME OVER");
            ls1.setSelectedIndex(4);
                }
        r1.setEnabled(false);
r2.setEnabled(false);
r3.setEnabled(false);
r4.setEnabled(false);
r5.setVisible(false);
r6.setVisible(false);
r7.setVisible(false);

b1.setVisible(false);
b2.setVisible(false);
l3.setVisible(false);
}//GEN-LAST:event_b1ActionPerformed

