    private void b2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b2ActionPerformed
        if(r5.isSelected()==true) {
            r1.setText(" ");
            r2.setText(" ");
        } else
            if(r6.isSelected()==true) {
                l2.setText(" ");
                r1.setText(" ");
                r2.setText(" ");
                r3.setText(" ");
                r4.setText(" ");
                l2.setText("Which of these cricketers played Test" +
                        " cricket for both India and Pakistan?  ");
                r1.setText("Dilawar Hussain");
                r2.setText("Jahangir Khan");
                r3.setText("Nasim-ul-Ghani");
                r4.setText("Amir Elahi");
            } else
                if(r7.isSelected()==true) {
                    r1.setText("26%");
                    r2.setText("10%");
                    r3.setText("30%");
                    r4.setText("34%");
                }
    }//GEN-LAST:event_b2ActionPerformed

