    private void b2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b2ActionPerformed
        if(r5.isSelected()==true) {
            r2.setText(" ");
            r4.setText(" ");
        } else
            if(r6.isSelected()==true) {
                l2.setText(" ");
                r1.setText(" ");
                r2.setText(" ");
                r3.setText(" ");
                r4.setText(" ");
                l2.setText("Who among these was never the president of" +
                        " Indian National Congress?  ");
                r1.setText("Richard Bourke");
                r2.setText("Henry Cotton");
                r3.setText("Alfred Webb");
                r4.setText("George Yule");
            } else
                if(r7.isSelected()==true) {
                    r1.setText("34%");
                    r2.setText("10%");
                    r3.setText("30%");
                    r4.setText("26%");
                }
}//GEN-LAST:event_b2ActionPerformed

