    private void b1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b1ActionPerformed
        if(r4.isSelected()==true) {
            t1.setText("Sorry,your answer is wrong." +
                    "You slip to Rs.12,50,000.");
            t2.setText("Correct Answer is Option 1");
           b5.setEnabled(true);
           ls1.setSelectedIndex(4);
           b6.setVisible(false);
        } else
            if(r1.isSelected()==true) {
                t1.setText("Congrats,your answer is correct." +
                        "You won Rs.1 CRORE......");
               b4.setEnabled(true);
               ls1.setSelectedIndex(1);
               b6.setVisible(false);
            } else
                if(r3.isSelected()==true) {
                    t1.setText("Sorry,your answer is wrong." +
                            "You slip to Rs.12,50,000.");
                    t2.setText("Correct Answer is Option 1");
                   b5.setEnabled(true);
                   ls1.setSelectedIndex(4);
                   b6.setVisible(false);
                } else

                {
            t1.setText("Sorry,your answer is wrong." +
                    "You slip to Rs.12,50,000.");
            t2.setText("Correct Answer is Option 1");
            b5.setEnabled(true);
            ls1.setSelectedIndex(4);
            b6.setVisible(false);
                }
        r1.setEnabled(false);
r2.setEnabled(false);
r3.setEnabled(false);
r4.setEnabled(false);
r5.setVisible(false);
r6.setVisible(false);
r7.setVisible(false);
b1.setVisible(false);
b2.setVisible(false);
l3.setVisible(false);
}//GEN-LAST:event_b1ActionPerformed

